import React from "react";
import "./Todos.css";

const Todos = ({ todos, deleteTodo, handleEdit }) => {
  handleEdit = (id) => {
    const todos = todos.filter((todo) => {
      return todo.id !== id;
    });
    // console.log(todos);

    const selectedContent = todos.find((item) => item.id === id);
    console.log(selectedContent);

    this.setState({
      todos,
      content: selectedContent.content,
    });
  };

  const todoList = todos.length ? (
    todos.map((todo) => {
      return (
        <div className="collection-item center row" key={todo.id}>
          <div className="flex">
            {/* <h5>
              <input
                className="col s12 m4 l3 order"
                type="text"
                value={todo.order}
                onChange={(e) => {
                  setUpdate(e.target.value);
                }}
              />
            </h5> */}
            <h5 className="cyan-text col s12 m7 l7">{todo.content}</h5>
            <button
              className="col s12 m2 l2 waves-effect waves-light btn-large"
              onClick={() => {
                handleEdit(todo.id);
              }}
            >
              Edit
            </button>
            <button
              className="col s12 m2 l2 waves-effect waves-light btn-large"
              onClick={() => {
                deleteTodo(todo.id);
              }}
            >
              Delete
            </button>
          </div>
        </div>
      );
    })
  ) : (
    <p className="center cyan-text">You have no todos left, Yay!!!</p>
  );
  return <div className="todos collection">{todoList}</div>;
};

export default Todos;
